const buttonContact=document.querySelector(".button--contacts"),popup=document.querySelector(".question"),popupClose=popup.querySelector(".question-close"),form=popup.querySelector("form"),nameInput=popup.querySelector("[name=name]"),emailInput=popup.querySelector("[type=email]"),textarea=popup.querySelector("textarea");let isStorageSupport=!0,storageName="",storageEmail="";try{storageName=localStorage.getItem("name"),storageEmail=localStorage.getItem("email")}catch(e){isStorageSupport=!1}buttonContact.addEventListener("click",e=>{e.preventDefault(),popup.classList.add("modal-show"),storageName?(nameInput.value=storageName,storageEmail?(emailInput.value=storageEmail,textarea.focus()):emailInput.focus()):nameInput.focus()}),popupClose.addEventListener("click",e=>{e.preventDefault(),popup.classList.remove("modal-show"),popup.classList.remove("modal-error")}),form.addEventListener("submit",e=>{nameInput.value&&emailInput.value?isStorageSupport&&(localStorage.setItem("name",nameInput.value),localStorage.setItem("email",emailInput.value)):(e.preventDefault(),popup.classList.remove("modal-error"),popup.offsetWidth=popup.offsetWidth,popup.classList.add("modal-error"))}),window.addEventListener("keydown",e=>{27===e.keyCode&&popup.classList.contains("modal-show")&&(e.preventDefault(),popup.classList.remove("modal-show"),popup.classList.remove("modal-error"))});